import { useState } from "react";
import { Form, Button, Modal, Image, Col, Row} from "react-bootstrap";

const PaymentWindow = ({show, onHide, sum, result}) => {
  return (
    //Модальное окно вывода результата
    <Modal
      show = {show}
      onHide = {onHide}
      size="lg"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Результат оплаты
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Row md={1}className="justify-content-center align-items-center d-flex">
         <Image width={100} height ={100} src = {'successMark.png'}></Image>
         <h1>Успешно</h1>
         </Row>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={onHide} variant='outline-success'>Ок</Button>
      </Modal.Footer>
    </Modal>
  );
}
export default PaymentWindow;
