import React, { useState } from "react";
import { Container } from "react-bootstrap";

const BasketPage = ({cart, setCart}) => {
  function handleOnClick({item}){
    return
  }
  const uniqueItemsData = cart.reduce((prev, item) => {
    const existingItem = prev.find(({itemId}) => itemId === item.itemId);
    if(existingItem)
        existingItem.qty = existingItem.qty + item.qty;
    else
        prev.push(item);
   return prev;
 }, []); //-> [ { id: 1, qty: 7 }, { id: 2, qty: 6 }, { id: 3, qty: 6 } ];
  return (
    <Container>
      <h1 className="d-flex justify-content-center align-content-center">Вы находитесь в корзине</h1>
      {cart.map((item) => {
        return(
          <div className="d-flex justify-content-center align-content-center">
          <h1>{item.name}</h1>
          <p>{item.description}</p>
          <button onClick={() => handleOnClick(item)}>Убрать товар</button>
          </div>
        )
      })}
    </Container>
  );
}

export default BasketPage;
