import {makeAutoObservable} from "mobx"

export default class userStore {
    constructor() {
        this._isAuth = true;
        this._user = {};
        makeAutoObservable(this);
    }

    setAuth(bool) {
        this._isAuth = bool;
    }
    setUser(bool) {
        this._user = bool;
    }
    get isAuth() {
        return this._isAuth
    }
    get user() {
        return this._user
    }
}
