import React, { useContext, useState } from "react";
import {Container, Row, Col, Card,Button} from "react-bootstrap";
import { Context } from "..";
import {Select} from "react-select"

function PaymentPage({cart, setCart}) {
    const [value, setValue] = useState('');
    const [expy, setExpy] = useState('');
    const [num, setNum] = useState('');
    const {bank} = useContext(Context);
    const [bankValue, setBank] = useState('');
    //Банк
    const handleBank = (e) =>{
      setBank(e.target.value)
      return;
    }
    //Номер карты
    const handleCardNum = (e)=>{
     let input = e.target.value;
    
    // Удаляем все символы, которые не являются числами
    input = input.replace(/\D/g, '');
    
    // Ограничение на 16 символов для карты
    if (input.length > 16) return;
    
    // Пробел через каждые 4 числа
    let formatted = '';
    for (let i = 0; i < input.length; i++) {
      if (i > 0 && i % 4 === 0) {
        formatted += ' ';
      }
      formatted += input[i];
    }
    setNum(formatted);       
    }

    //Срок действия
    const handleDate= (e)=>{
    const input = e.target.value;

    //Разрешенные символы
    if (!/^[0-9/]*$/.test(input)) 
        return;
    //Ограничение длины
    if (input.length > 5) 
        return;
    //Вставка слэша
    if (input.length === 2 && !input.includes('/')) {
        setExpy(input + '/');
        return;
    }
    // Проверка на только один слэш
    if ((input.match(/\//g) || []).length > 1) 
        return;
    setExpy(input);     
    }
    
    //CVC
    const handleCVC = (e)=>{
    const input = e.target.value;
    
    // Разрешить только числа
    if (!/^\d*$/.test(input)) 
        return;
    
    // Максимальная длина - 3 символа
    if (input.length > 3) 
        return;  
    setValue(input);
    }
  return (
    <Container fluid>
      <Row className="d-flex justify-content-center mt-3">
          <Card className="mt-5 d-flex flex-column align-items-center justify-content-around"
    style={{width:400, height:550, fontSize:32, border:'3px solid black'}}>
        <h1>Оплата</h1>
        <Col className="mt-2">
        <h3>Банк</h3>
            <div className="d-flex ">
                {bank.banks.map(bank =>
                  <Select onChange={handleBank} options={bank.name} style = {{width:'200px'}}key = {bank.bakId} />
                )}
            </div>      
            <h3>Номер карты</h3>
            <div className="d-flex flex-column align-items-center justify-content-center   ">
            <input
            type="text"
            value={num}
            onChange={handleCardNum}
            placeholder="1111 1111 1111 1111"
            />
            </div>
            <h3>Дата</h3>
            <div className="mt-3 d-flex flex-column align-items-center justify-content-center   ">
            <input
            type="text"
            value={expy}
            onChange={handleDate}
            placeholder="dd/mm"
            />
            </div>
            <h3>CVC код</h3>
            <div className="mt-3 d-flex flex-column align-items-center justify-content-center   ">
            <input
            type="text"
            value={value}
            onChange={handleCVC}
            maxLength={3}
            placeholder="000"
            />         
            </div>
        </Col>
        <Container className="d-flex justify-content-center align-items-center">
        <Button variant="outline-danger" className="me-4 mb-4">Отменить</Button>
        <Button variant="outline-success" className="ms-4 mb-4">Подтвердить</Button>
        </Container>
        </Card>
      </Row>
    </Container>
  );
}
export default PaymentPage;
