import { observer } from "mobx-react-lite";
import React, { useContext, useEffect, useState } from "react";
import { Button, Col, Container, Row, Image } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Context } from "../index.js";
import { getBasket } from "../http/basketAPI.js";
import { getOneItem } from "../http/itemAPI.js";

const BasketPage = observer(({cart, setCart}) => {
  let finalCost = 0;
  const [item, setItem] = useState([]);
  const {basket} = useContext(Context);
  const [check, setCheck] = useState(1);
  
 const loadBasketItems = async() =>  {    //Получение данных о корзине  
            const itemIds = await getBasket(basket.basketId);
            
            const itemsPromises = itemIds.map(id => getOneItem(id.itemId));
            const items = await Promise.all(itemsPromises);
            
            basket.setBasketItems(itemIds);
            basket.setItems(items);
            
            return items;
    }
    useEffect(() => {
      let data =[]
      loadBasketItems().then(() => {  

      basket.items.map(value => {
      setCart((PrevState) => ([...PrevState, value]))
      data.push(value);
      })
      
    }).finally(() => {
      console.log(cart);
    })

    }, [])
    

  function stockBasket(item){
    const itemExists = cart.find((product) => product.itemId === item.itemId);
    if (itemExists){
      setCart(cart.map((singleItem) => {
        return singleItem.itemId === item.itemId 
        ? {...itemExists, quantity:itemExists.quantity + 1}
        : singleItem;
      }));
    } else{
     setCart([...cart, {...item, quantity:1}]);
    }
  }  
  const navigate = useNavigate()
  function getCost(){
    cart.map((product) => finalCost+= product.quantity * product.price)
    return
  }

    function handleIncrease({item}){  //Добавление товаров 
    const itemExists = cart.find((product) => product.itemId === item.itemId);  //Проверка на наличие товара в корзине
    if (itemExists) { //Добавляем один товар
      setCart(
        cart.map((singleItem) =>
          singleItem.itemId === item.itemId
            ? { ...itemExists, quantity: itemExists.quantity + 1 }
            : singleItem
        )
      );
    }
  }

    function handleDecrease({item}){  //Уменьшение товаров
    const selectedItem = cart.find((product) => product.itemId === item.itemId);//Проверка на наличие товара в корзине
    if (selectedItem.quantity === 1) {  //Удаляем товар из корзины, если удаляют последний
      setCart(
        cart.filter((oneItem) => oneItem.itemId !== selectedItem.itemId)
      );
    } else {    //Уменьшаем кол-во товаров в корзине
      setCart(
        cart.map((singleItem) =>
          singleItem.itemId === item.itemId
            ? { ...selectedItem, quantity: selectedItem.quantity - 1 }
            : singleItem
        )
      );
    }
  }


  return (
    <Container>
      <h1 className="d-flex justify-content-center align-content-center">Вы находитесь в корзине</h1>
      {basket.items.map((item) => {
        return(
          <Row>
          <Col className="d-flex justify-content-center align-content-center">
          <Container  className="d-flex justify-content-center align-content-center mt-4">
          <Image width = {50} height = {50} src = {'http://localhost:5000/' + item.img}/>
          <h1 className="d-flex me-2">{item.name}</h1>
          <h1 className="d-flex me-2">{item.price} Руб.</h1>
          <Button onClick={() => handleDecrease({item})} variant="secondary" className="me-2" style={{width:'40px'}}>-</Button>
          <h1 className="d-flex me-2">x{item.quantity}</h1>
          <Button onClick={() => handleIncrease({item})} variant="secondary" className="ms-2" style={{width:'40px'}}>+</Button>
          </Container>
          </Col>
          </Row>
        )
      })}
      {cart.length > 0 ?
      <div onLoad = {() =>{getCost();}}className="d-flex justify-content-center align-content-center mt-4">
        <h2>Итоговая стоимость: {finalCost.toFixed(2)} Руб.</h2>
        <Button variant={"outline-dark ms-4"} 
            onClick={() => navigate('/payment')}
            >
              Оплатить</Button>
      </div>
      :
      <></>
      }

    </Container>
  );
})

export default BasketPage;
