import {makeAutoObservable} from "mobx"

export default class basketStore {
    constructor() {
        this._basketItems = [];
        makeAutoObservable(this);
    }

    setBasketItems(items) {
        this._basketItems = items;
    }

    get basketItems() {
        return this._basketItems;
    }
}
