import './App.css';
import {BrowserRouter} from "react-router-dom"
import AppRouter from './components/AppRouter';
import NavBar from './components/navBar.js'
import { observer } from 'mobx-react-lite';
import { useContext } from 'react';
import { Context } from './index.js';
import { useState } from 'react';
import { useEffect } from 'react';
import { auth } from './http/userAPI.js';
import { Spinner } from 'react-bootstrap';
import { getAllBanks } from './http/bankAPI.js';

const App = observer(() => {
  const {user} = useContext(Context);
  const [load, setLoad] = useState(true);

  useEffect(() => {       //Сохранение авторизации пользователя при перезагрузке страницы
    setTimeout(() => {
    auth().then(data => {
      user.setUser(true);
      user.setAuth(true);
    },(data => {
      user.setUser({});
      user.setAuth(false);
    }) ).finally(() => setLoad(false))
    }, 1000)

  })
      const {bank} = useContext(Context)
    useEffect(() => {               //Получение товаров с сервера
    getAllBanks().then(data => {
      bank.setBanks(data.rows);
    }, )
  });
  if (load) {
    return <Spinner animation={'grow'}/>
  }
  return (
    <BrowserRouter>
    <NavBar/>
    <AppRouter/>
    </BrowserRouter>
  );
})

export default App;
