import React, { useContext } from "react";
import { Button, Card, Col, Container, Image, Row } from "react-bootstrap";
import { Context } from "..";
import { observer } from "mobx-react-lite";

const ItemPage = observer(({cart, setCart}) => {
  const item =  {itemId:1, name:"Зонт", price:1000.99, description:"Зонт, зонтик (от нидерл. zonnedoek, zondek — тент, навес от солнца, буквально «защита от солнца») — устройство, предназначенное для защиты человека от дождя или от солнечных лучей. Первые зонты предназначались именно для защиты от солнца, а не от дождя[1]", img:`https://www.modi.ru/upload/product/9fd/xuuc81njybjnltrj6471whd81gcpywxr.jpg`};
  const {user} = useContext(Context)
  function handleClick(){
    setCart([...cart, item]);
  }
 return (
<Container className="mt-3">
  <Row>

<Col md={4}>
    <Image width = {400} height = {400} src = {item.img}/>
  </Col>

  <Col md={4} className="d-flex flex-column justify-content-center align-items-center">
      <h1>{item.name}</h1>
  </Col>

  {user.isAuth ?
  <Col md={4}>
    <Card className="mt-5 d-flex flex-column align-items-center justify-content-around"
    style={{width:300, height:300, fontSize:32, border:'3px solid black'}}>
    <h3>{item.price} Руб.</h3>
    <Button variant={"outline-dark"} onClick={handleClick()}>
      {cart.find((Item) => Item.name === item.name) ? "Добавлено в корзину" : "Добавить в корзину"}</Button>
    </Card>
  </Col>
  :
    <Col md={4}>
    <Card className="mt-5 d-flex flex-column align-items-center justify-content-around"
    style={{width:300, height:300, fontSize:32, border:'3px solid black'}}>
    <h3 className="ms-4">Войдите в аккаунт для покупки</h3>
    </Card>
  </Col>
  }

  </Row>
  <Row className="d-flex flex-column m-3">
    <h1>Описание товара</h1>
    <div style={{ fontSize:18}}>{item.description}</div>
  </Row>
</Container>
  );
})

export default ItemPage;
