import React, { useContext, useEffect, useState } from "react";
import {Routes, Route, Navigate} from "react-router-dom"
import { AdminRoutes, AuthRoutes, PublicRoutes } from "../Routes";
import { Context } from "../index.js";
import { getBasket } from "../http/basketAPI.js";
import { getOneItem } from "../http/itemAPI.js";


const AppRouter = () => {
  const {user} = useContext(Context);
  const {basket} = useContext(Context);
  const [cart, setCart] = useState([])  //Отслеживание состояния корзины
  useEffect(() => {
      const loadBasketItems = async() =>  {    //Получение данных о корзине  
          const itemIds = await getBasket(basket.basketId);
          const itemsPromises = itemIds.map(id => getOneItem(id.itemId));
          const items = await Promise.all(itemsPromises);
                    
          basket.setBasketItems(itemIds);
          basket.setItems(items);
          return items;
          }
          loadBasketItems()
  }, [cart]);
  return (
    //Список путей сайта
    <Routes>    
      {user.isAuth === true && AuthRoutes.map(({path, Component}) =>  //Авторизованный аккаунт
       <Route key = {path} path={path} element={<Component cart = {cart} setCart = {setCart} />} />
      )}
      {user.isAuth === true && user.role === 'ADMIN' && AdminRoutes.map(({path, Component}) =>  //Авторизованный аккаунт
       <Route key = {path} path={path} element={<Component cart = {cart} setCart = {setCart} />} />
      )}
      {PublicRoutes.map(({path, Component}) =>  //Общедоступные пути
      <Route key = {path} path={path} element={<Component cart = {cart} setCart = {setCart}/>} /> 
      )}
      <Route path="*" element={<Navigate to="/" replace cart = {cart} setCart = {setCart}/>}/>
    </Routes>
  );
}

export default AppRouter;
