import { observer } from "mobx-react-lite";
import React, { useContext, useEffect, useState } from "react";
import { Button, Col, Container, Row, Image } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Context } from "../index.js";
import { addItem, deleteItemInBasket } from "../http/basketAPI.js";


const BasketPage = observer(({cart, setCart}) => {
  const [item, setItem] = useState([]);
  const {basket} = useContext(Context);
  const [uniqueProducts, setUniqueProducts] = useState([]);
  const [finalCost, setCost] = useState(0);

  useEffect(() => {
    // Считаем количество для каждого товара
    const productCounts = cart.reduce((acc, product) => {
      const existingProduct = acc.find((p) => p.itemId === product.itemId); //Проверяем все товары из корзины по id
      if (existingProduct) {
        existingProduct.quantity += 1;
      } else {
        acc.push({ ...product, quantity: 1 });
      }
      return acc;
    }, []);

    setUniqueProducts(productCounts);
  }, []); 
  useEffect(() => {
     const loadBasketItems = async() =>  {    //Получение данных о корзине  
                const itemIds = await getBasket(basket.basketId);
                
                const itemsPromises = itemIds.map(id => getOneItem(id.itemId));
                const items = await Promise.all(itemsPromises);
                
                await basket.setBasketItems(itemIds);
                await basket.setItems(items);
              let data = [];
              basket.items.map(value => {
              data.push(value);
              })
           
          setCart(data);     
          console.log(cart);           
          return items;
        }
      loadBasketItems()
  }, [uniqueProducts.length])
  
  const navigate = useNavigate()

  useEffect(() => {
      getCost();
  }, [uniqueProducts])

  function getCost(){     //Подсчет стоимости товаров
    var cost = 0;
    uniqueProducts.map((product) => {
      cost += product.price * product.quantity
    })
    setCost(cost);
    return
  }

    function handleIncrease({item}){  //Добавление товаров 
    const itemExists = uniqueProducts.find((product) => product.itemId === item.itemId);  //Проверка на наличие товара в корзине
    if (itemExists) { //Добавляем один товар
      addItem(basket.basketId, itemExists.itemId);
      setCart([...cart, itemExists]);
      setUniqueProducts(
        uniqueProducts.map((singleItem) =>
          singleItem.itemId === item.itemId
            ? { ...itemExists, quantity: itemExists.quantity + 1 }
            : singleItem
        )
      );
    }
  }

    function handleDecrease({item}){  //Уменьшение товаров
    const selectedItem = uniqueProducts.find((product) => product.itemId === item.itemId);//Проверка на наличие товара в корзине

    if (selectedItem.quantity === 1) {  //Удаляем товар из корзины, если удаляют последний
      deleteItemInBasket(basket.basketId, selectedItem.itemId);
      setUniqueProducts(
        uniqueProducts.filter((oneItem) => oneItem.itemId !== selectedItem.itemId)
      );
    } else {    //Уменьшаем кол-во товаров в корзине
      deleteItemInBasket(basket.basketId, selectedItem.itemId);
      setUniqueProducts(
        uniqueProducts.map((singleItem) =>
          singleItem.itemId === item.itemId
            ? { ...selectedItem, quantity: selectedItem.quantity - 1 }
            : singleItem
        )
      );
    }
    basket.setQuantity(basket.quantity+1);
  }




  return (
    <Container>
      <h1 className="d-flex justify-content-center align-content-center">Вы находитесь в корзине</h1>
      {uniqueProducts.map((item) => {
        return(
          <Row>
          <Col className="d-flex justify-content-center align-content-center">
          <Container  className="d-flex justify-content-center align-content-center mt-4">
          <Image width = {50} height = {50} src = {'http://localhost:5000/' + item.img}/>
          <h1 className="d-flex me-2">{item.name}</h1>
          <h1 className="d-flex me-2">{item.price} Руб.</h1>
          <Button onClick={() => handleDecrease({item})} variant="secondary" className="me-2" style={{width:'40px'}}>-</Button>
          <h1 className="d-flex me-2">x{item.quantity}</h1>
          <Button onClick={() => handleIncrease({item})} variant="secondary" className="ms-2" style={{width:'40px'}}>+</Button>
          </Container>
          </Col>
          </Row>
        )
      })}
      {uniqueProducts.length > 0 ?
      <div className="d-flex justify-content-center align-content-center mt-4">
        <h2>Итоговая стоимость: {finalCost.toFixed(2)} Руб.</h2>
        <Button variant={"outline-dark ms-4"} 
            onClick={() => navigate('/payment')}
            >
              Оплатить</Button>
      </div>
      :
      <></>
      }

    </Container>
  );
})

export default BasketPage;
