import {$host, $authHost} from "./index.js"
import CryptoJS from 'crypto-js';

const encryptPayload = (payload, secretKey) => {
  const ciphertext = CryptoJS.AES.encrypt(JSON.stringify(payload), secretKey).toString();
  return ciphertext;
};
export const transactionPayment = async(payment) => {
   try{
   const encryptedPayload = encryptPayload(payment, process.env.REACT_APP_SECRET_KEY);
    const {data} = await $authHost.post('api/transaction', payment);
    return data;
   } catch(e){
    console.log(e.response.data.message);
    return e.response.data.message
   }
}
