import React from "react";
import { Col, Container } from "react-bootstrap";

const BasketPage = ({cart, setCart}) => {
  function handleOnClick({item}){
    setCart(cart.filter((product) => product.name !== item.name))
    return
  }
  return (
    <Container>
      <h1 className="d-flex justify-content-center align-content-center">Вы находитесь в корзине</h1>
      {cart.map((item) => {
        return(
          <Col className="d-flex justify-content-center align-content-center">
          <Container  className="d-flex justify-content-center align-content-center mt-4">
          <h1 className="d-flex me-2">{item.name}</h1>
          <h1 className="d-flex me-2">{item.price}</h1>
          <button onClick={() => handleOnClick(item)}>Убрать товар</button>
          </Container>
          </Col>
        )
      })}
    </Container>
  );
}

export default BasketPage;
