import React from "react";
import { Card, Col, Container, Image, Row } from "react-bootstrap";

const ItemPage = () => {
  const item =  {itemId:1, name:"Зонт", price:1000.99, description:"Хороший зонт", img:`https://www.modi.ru/upload/product/9fd/xuuc81njybjnltrj6471whd81gcpywxr.jpg`};
  return (
<Container className="mt-3">
  <Row>

<Col md={4}>
    <Image width = {400} height = {400} src = {item.img}/>
  </Col>

  <Col md={4} className="d-flex flex-column justify-content-center align-items-center">
      <h1>{item.name}</h1>
      <h6>{item.description}</h6>
  </Col>

  <Col md={4}>
    <Card className="d-flex flex-column align-items-center justify-content-around"
    style={{width:300, height:300, fontSize:32, border:'3px solid black'}}>

    </Card>
  </Col>

  </Row>
  
</Container>
  );
}

export default ItemPage;
