import { values } from "mobx";
import React, { useState } from "react";
import {Container, Row, Col, Card, Form, Button} from "react-bootstrap";

function PaymentPage() {
    const [value, setValue] = useState('');
    const [expy, setExpy] = useState('');
    const [num, setNum] = useState('');
    
    const handleCardNum = (e)=>{
        
    }
    const handleDate= (e)=>{
     const input = e.target.value;
    
    // Allow only digits and slash
    if (!/^[0-9/]*$/.test(input)) return;
    
    // Enforce max length of 5 characters (dd/mm)
    if (input.length > 5) return;
    
        // Auto-insert slash after 2 digits
        if (input.length === 2 && !input.includes('/')) {
        setExpy(input + '/');
        return;
        }
        // Prevent more than one slash
        if ((input.match(/\//g) || []).length > 1) return;
        setExpy(input);
        
    }
    const handleCVC = (e)=>{
    const input = e.target.value;
    
    // Allow only digits
    if (!/^\d*$/.test(input)) return;
    
    // Enforce max length of 3
    if (input.length > 3) return;
    
    setValue(input);
    }
  return (
    <Container fluid>
      <Row className="d-flex justify-content-center mt-3">
          <Card className="mt-5 d-flex flex-column align-items-center justify-content-around"
    style={{width:400, height:550, fontSize:32, border:'3px solid black'}}>
        <h1>Оплата</h1>
        <Col className="mt-2">
            <h3>Банк</h3>
            <div className="d-flex flex-column align-items-center justify-content-center   ">
            <input
            type="text"
            value={expy}
            onChange={handleDate}
            placeholder="1111 1111 1111 1111"
            />
            </div>      
            <h3>Номер карты</h3>
            <div className="d-flex flex-column align-items-center justify-content-center   ">
            <input
            type="text"
            value={expy}
            onChange={handleDate}
            placeholder="1111 1111 1111 1111"
            />
            </div>
            <h3>Дата</h3>
            <div className="mt-3 d-flex flex-column align-items-center justify-content-center   ">
            <input
            type="text"
            value={expy}
            onChange={handleDate}
            placeholder="dd/mm"
            />
            </div>
            <h3>CVC код</h3>
            <div className="mt-3 d-flex flex-column align-items-center justify-content-center   ">
            <input
            type="text"
            value={value}
            onChange={handleCVC}
            maxLength={3}
            placeholder="000"
            />         
            </div>
        </Col>
                <Button>Подтвердить</Button>
        </Card>
      </Row>
    </Container>
  );
}
export default PaymentPage;
