import {makeAutoObservable} from "mobx"

export default class itemStore {
    constructor() {
        // TODO Add cards, transactions
        this._items = []
        this._page = 1;
        this._totalitems = 0;
        this._limit = 3;
        makeAutoObservable(this);
    }

    setItems(items) {
        this._items = items;
    }
    setPage(page) {
        this._page = page;
    }
    setTotal(total) {
        this._totalitems = total;
    }
    setLimit(limit) {
        this._limit = limit;
    }    
    get items() {
        return this._items;
    }
}
